properties.scriptBody:
$xmlPath = "${p:agent/AGENT_HOME}\var\work\${p:appStack}_CFG_${p:application/appName}"
$xml = "MSMQServer.xml"
$xmlFile = join-path $xmlPath $xml
if ("${p:envName}" -ne "PROD") { 
$xml = "nonprod.MSMQServer.xml" 
$testPath = join-path $xmlPath $xml
if (test-path $testPath -pathType leaf) { $xmlFile = $testPath }
}
$xml = "${p:envName}.MSMQServer.xml" 
$testPath = join-path $xmlPath $xml
if (test-path $testPath -pathType leaf) { $xmlFile = $testPath }
if (test-path $xmlFile -pathType leaf) {
write-host "XML file used: $xmlFile"
${p:agent/AGENT_HOME}\var\work\${p:system/PowershellScriptDir}\STIG-ServerStandup.ps1 -XMLFile $xmlFile; exit $LASTEXITCODE
} else { write-host "No MSMQ XML ($xmlFile) found, skipping."; exit 0 }

$xmlPath = "${p:agent/AGENT_HOME}\var\work\${p:appStack}_CFG_${p:appName}"
$xml = "Monitor.xml"
$xmlFile = join-path $xmlPath $xml
$testPath = join-path $xmlPath ${p:xmlMonitorOverride}
if (test-path $testpath -pathType leaf) { $xmlFile = $testPath }
if (test-path $xmlFile -pathType leaf) {${p:agent/AGENT_HOME}\var\work\${p:system/PowershellScriptDir}\SCOM\SCOM_GenMonitorMP.ps1 -XMLFile $xmlFile -OutputDir ${p:scomxml_basedir}\${p:appStack}\${p:appName}\${p:resource.name}}; exit $LASTEXITCODE

$xmlPath = "${p:agent/AGENT_HOME}\var\work\${p:appStack}_CFG_${p:appName}"
$xml = "Monitor.xml"
$xmlFile = join-path $xmlPath $xml
$testPath = join-path $xmlPath ${p:xmlMonitorOverride}
if (test-path $testpath -pathType leaf) { $xmlFile = $testPath }
if (test-path $xmlFile -pathType leaf) {${p:agent/AGENT_HOME}\var\work\${p:system/PowershellScriptDir}\udMonitor\udInvokeMonitor.ps1 -XMLFile $xmlFile} ; exit $LASTEXITCODE

$xmlPath = "${p:agent/AGENT_HOME}\var\work\${p:appStack}_CFG_${p:application/appName}"
$xml = "WebServer.xml"
$xmlFile = join-path $xmlPath $xml
if ("${p:envName}" -ne "PROD") { 
$xml = "nonprod.WebServer.xml" 
$testPath = join-path $xmlPath $xml
if (test-path $testPath -pathType leaf) { $xmlFile = $testPath }
}
$xml = "${p:envName}.WebServer.xml" 
$testPath = join-path $xmlPath $xml
if (test-path $testPath -pathType leaf) { $xmlFile = $testPath }
write-host "XML file used: $xmlFile"
${p:agent/AGENT_HOME}\var\work\${p:system/PowershellScriptDir}\STIG-ServerStandup.ps1 -XMLFile $xmlFile; exit $LASTEXITCODE
